mapper.update:
update mybatis_class_tbl
   set class_name = #{className,jdbcType=VARCHAR},
       teacher_id = #{teacherId,jdbcType=BIGINT}
 where class_id = #{classId,jdbcType=BIGINT}

mapper.select:
select class_id, class_name, teacher_id
  from mybatis_class_tbl
 where class_id = #{classId,jdbcType=BIGINT}
